using System;namespace text_based_calculator_LITE{class Program{static void Main(string[] args){string q="Input:";string o="Operator:";string f="Wrong Operator";Console.WriteLine(q);double x=Convert.ToDouble(Console.ReadLine());Console.WriteLine(o);string k=Console.ReadLine();Console.WriteLine(q);double y=Convert.ToDouble(Console.ReadLine());double a=x+y;double s=x-y;double m=x*y;double d=x/y;if(k=="+"){Console.WriteLine(a);}else{if(k=="-"){Console.WriteLine(s);}else{if(k=="*"||k=="x"||k=="X"){Console.WriteLine(m);}else{if(k=="/"){Console.WriteLine(d);}else{Console.WriteLine(f);}}}}Console.ReadLine();}}}